using System.Collections; using System.Collections.Generic; using UnityEngine;  public class Boat : MonoBehaviour  {     private Rigidbody rbody;     public float turnSpeed = 1000f;     public float accelerateSpeed = 1000f;      public bool inWindZone = false;     public GameObject windZone;     public TackGybeController a;       // Use this for initialization  void Start ()      {         rbody = GetComponent<Rigidbody>();     }       // Update is called once per frame  void Update ()      {         float h = Input.GetAxis("Horizontal");             float v = Input.GetAxis("Vertical");          rbody.AddTorque(0f,h*turnSpeed*Time.deltaTime,0f); // add rotation to the boat in the y axis          rbody.AddForce(transform.forward*v*accelerateSpeed*Time.deltaTime);//          //set value on POS dial          POS.showDialPosition(rbody.velocity.magnitude, 0, 100);    }     // on entrance of the windzone area      void OnTriggerEnter(Collider coll)     {         if(coll.gameObject.tag=="windarea")         {             windZone = coll.gameObject;             inWindZone = true;          }      }      // on exit of the wind zone area     void OnTriggerExit(Collider coll)     {         if(coll.gameObject.tag=="windarea")         {             inWindZone = false;         }     }     // persistently apply a windforce on the rigidbody of the dinghy     private void FixedUpdate()     {         if (inWindZone)         {             rbody.AddForce(windZone.GetComponent<WindArea>().direction * windZone.GetComponent<WindArea>().strength);         }     }      //on collision with a waypoint transform the boat trigger msg to prompt buttons     void OnCollisionEnter(Collision collision)     {         if (collision.gameObject.tag == "waypoints")         {             var msg = GameObject.Find("Canvas/Prompts/Panel");             msg.SetActive(true);             a = new TackGybeController(1);           }         if (collision.gameObject.tag == "waypoint2")         {             var msg = GameObject.Find("Canvas/Prompts/Panel");             msg.SetActive(true);             a = new TackGybeController(2);          }         if (collision.gameObject.tag == "waypoint3")         {             var msg = GameObject.Find("Canvas/Prompts/Panel");             msg.SetActive(true);             a = new TackGybeController(3);          }          if (collision.gameObject.tag == "waypoint4")         {             var msg = GameObject.Find("Canvas/Prompts/CompletePanel");             msg.SetActive(true);         }      } } 